let sentTracking = false;
window.addEventListener('scroll', function() {
    const scrollTop = window.scrollY; // how far the user has scrolled
    const windowHeight = window.innerHeight;
    const docHeight = document.documentElement.scrollHeight;

    const scrollPercent = (scrollTop + windowHeight) / docHeight * 100;

    if (scrollPercent >= 50 && !sentTracking) {
      // tracking script
      window['optimizely'] = window['optimizely'] || [];
      window['optimizely'].push({
        type: "event",
        eventName: "scroll_50",
        tags: {
          revenue: 0, // Optional in cents as integer (500 == $5.00)
          value: 0.00 // Optional as float
        }
      });
      sentTracking = true; // ensure it only triggers once
    }
});



document.addEventListener("DOMContentLoaded", () => {
    MktoForms2.whenReady(function (form) {
        form.onSuccess(function () {
            console.log("success");
            //your tracking success code here
        });
        form.onSubmit(function () {
            console.log("submit");
            //your tracking submit (both success and error) code here
        });
        form.onValidate(function (isFormValid) {
            //make sure the form is fully validated before tracking
            setTimeout(() => {
                let lastRecaptchaUserInput = "",
                    formElm = form.getFormElem();

                if(formElm.length) {
                    const lastRecaptchaUserInputElm = formElm[0].querySelector("input[name=lastRecaptchaUserInput]");
                    if(lastRecaptchaUserInputElm) {
                        lastRecaptchaUserInput = lastRecaptchaUserInputElm.value.trim();
                    }
                }

                if(isFormValid && lastRecaptchaUserInput) {
                    //your tracking validate success code here
                    window['optimizely'] = window['optimizely'] || [];
                    window['optimizely'].push({
                    type: "event",
                    eventName: "contact_us_fill",
                        tags: {
                            revenue: 1, // success | Optional
                            value: 1.00 // Optional as float
                        }
                    });

                    console.log("form is valid and recaptcha is ok");
                } else {
                    //your tracking validate error code here
                    window['optimizely'] = window['optimizely'] || [];
                    window['optimizely'].push({
                    type: "event",
                    eventName: "contact_us_fill",
                        tags: {
                            revenue: 0, // error | Optional
                            value: 0.00 // Optional as float
                        }
                    });
                    console.log("form is invalid or recaptcha is not ok");
                }
            }, 50);
        });
    });
});
